import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../assets/css/others/Allworkouts.css';

const Allworkouts = () => {
  const [Allworkouts, setAllworkouts] = useState([]);
  const [newWorkout, setNewWorkout] = useState({
    name: '',
    description: '',
    exercises: [{ name: '', reps: '' }]
  });

  useEffect(() => {
    const fetchAllworkouts = async () => {
      try {
        const response = await axios.get('http://localhost:5001/Allworkouts');
        setAllworkouts(response.data);
      } catch (error) {
        console.error('Error fetching Allworkouts:', error);
      }
    };

    fetchAllworkouts();
  }, []);

  const handleWorkoutChange = (e) => {
    const { name, value } = e.target;
    setNewWorkout(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleExerciseChange = (index, e) => {
    const { name, value } = e.target;
    const exercises = [...newWorkout.exercises];
    exercises[index] = { ...exercises[index], [name]: value };
    setNewWorkout({ ...newWorkout, exercises });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/Allworkouts', newWorkout, {
        headers: { 'Content-Type': 'application/json' }
      });
      setAllworkouts(prevState => [...prevState, response.data]);
      setNewWorkout({
        name: '',
        description: '',
        exercises: [{ name: '', reps: '' }]
      });
    } catch (error) {
      console.error('Error adding workout:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/Allworkouts/${id}`);
      setAllworkouts(prevState => prevState.filter(workout => workout.id !== id));
    } catch (error) {
      console.error('Error deleting workout:', error);
    }
  };

  return (
    <div className="container">
      <div className="header">Workout Plans</div>
      <div className="workout-list">
        {Allworkouts.map((workout) => (
          <div key={workout.id} className="workout-item">
            <h3>{workout.name}</h3>
            <p>{workout.description}</p>
            <ul>
              {workout.exercises.map((exercise, index) => (
                <li key={index}>
                  {exercise.name} - {exercise.reps} reps
                </li>
              ))}
            </ul>
            <button onClick={() => handleDelete(workout.id)}>Delete</button>
          </div>
        ))}
      </div>
      <div className="add-workout-form">
        <h3>Add a New Workout</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Workout Name"
            value={newWorkout.name}
            onChange={handleWorkoutChange}
            required
          />
          <textarea
            name="description"
            placeholder="Workout Description"
            value={newWorkout.description}
            onChange={handleWorkoutChange}
            required
          ></textarea>
          {newWorkout.exercises.map((exercise, index) => (
            <div key={index}>
              <input
                type="text"
                name="name"
                placeholder="Exercise Name"
                value={exercise.name}
                onChange={(e) => handleExerciseChange(index, e)}
                required
              />
              <input
                type="number"
                name="reps"
                placeholder="Reps"
                value={exercise.reps}
                onChange={(e) => handleExerciseChange(index, e)}
                required
              />
            </div>
          ))}
          <button type="submit">Add Workout</button>
        </form>
      </div>
    </div>
  );
};

export default Allworkouts;
